import React, { Component } from 'react';


class App extends Component {
  constructor(props){
    super(props)

    this.state={
      newItem:"",
      list:[]

    }
  }

  addItem(){
    const newItem={
      id: 1 + Math.random,
      value: this.state.newItem.slice()
    };
    const list = [...this.state.lsit];
    list.push(newItem);
    this.setState({
      list,
      newItem:""

    });


  }


  render() {
    return (
      <div className="App">
          <div>
            Add an Item...
            <br/>
            <input
              type="text"
              placeholder="Type Item here..."
              value={this.state.newItem}
              onChange={e=> this.update("newItem", e.target.value)}
            />
            <button
              onClick={() => this.addItem()}
            >
              Add
            </button>
          </div>
      </div>

    )



  }




}

export default App;
